import { useState } from 'react';
import Header from './components/Header';
import CalendarGrid from './components/CalendarGrid';
import AllMembersCalendar from './components/AllMembersCalendar';
import UserStatus from './components/UserStatus';
import { useAuth } from './context/AuthContext';
import './App.css';

function App() {
  const { user, loading } = useAuth();
  const [selectedMember, setSelectedMember] = useState(null);
  const [viewAllMembers, setViewAllMembers] = useState(false);

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="app">
      <Header selectedMember={selectedMember} setSelectedMember={setSelectedMember} />
      <UserStatus />

      <button
        className="toggle-view-btn"
        onClick={() => setViewAllMembers(!viewAllMembers)}
      >
        {viewAllMembers ? 'View Single Member' : 'View All Members'}
      </button>

      <main>
        {user && (
          viewAllMembers ? (
            <AllMembersCalendar />
          ) : (
            selectedMember && (
              <CalendarGrid
                selectedMember={selectedMember}
                setSelectedMember={setSelectedMember}
              />
            )
          )
        )}
      </main>
    </div>
  );
}

export default App;
