import React from 'react';
import { useAuth } from '../context/AuthContext';

const UserStatus = () => {
  const { user } = useAuth();

  if (!user) return null;

  return (
    <div className="user-status">
      <div className="status-content">
        <div className="status-header">
          <div className="user-info">
            <div className="user-avatar">
              {user.photoURL ? (
                <img src={user.photoURL} alt={user.displayName} />
              ) : (
                <span>{user.displayName?.[0] || user.email[0]}</span>
              )}
            </div>
            <div className="user-details">
              <h3>{user.displayName || 'User'}</h3>
              <p>{user.email}</p>
            </div>
          </div>
          <div className="status-indicator online">
            <span className="status-dot"></span>
            <span>Online</span>
          </div>
        </div>
      </div>

      <style jsx>{`
        .user-status {
          background: white;
          border-radius: var(--border-radius);
          padding: 1.5rem;
          margin-bottom: 1.5rem;
          box-shadow: var(--shadow);
        }

        .status-content {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }

        .status-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .user-info {
          display: flex;
          align-items: center;
          gap: 1rem;
        }

        .user-avatar {
          width: 48px;
          height: 48px;
          border-radius: 50%;
          background: var(--primary-color);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-size: 1.2rem;
          font-weight: 500;
          overflow: hidden;
        }

        .user-avatar img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .user-details h3 {
          margin: 0;
          color: var(--text-color);
          font-size: 1.1rem;
          font-weight: 600;
        }

        .user-details p {
          margin: 0;
          color: #666;
          font-size: 0.9rem;
        }

        .status-indicator {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          font-size: 0.9rem;
          padding: 0.5rem 1rem;
          border-radius: 20px;
          background: var(--secondary-color);
        }

        .status-indicator.online {
          color: var(--success-color);
        }

        .status-dot {
          width: 8px;
          height: 8px;
          border-radius: 50%;
          background: currentColor;
        }

        @media (max-width: 768px) {
          .user-status {
            padding: 1rem;
            margin-bottom: 1rem;
          }

          .status-header {
            flex-direction: column;
            align-items: flex-start;
            gap: 1rem;
          }

          .status-indicator {
            align-self: flex-start;
          }
        }
      `}</style>
    </div>
  );
};

export default UserStatus;
