import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { ArrowLeft, Menu, Search, AlertCircle } from 'react-feather';
import googleLogo from '../assets/google-logo.svg';
import Logo from './Logo';

export default function Header({ selectedMember, setSelectedMember }) {
  const { user, logout, timezone, setTimezone } = useAuth();
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('name'); // 'name' or 'email'
  const [sortOrder, setSortOrder] = useState('asc'); // 'asc' or 'desc'

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        setLoading(true);
        setError(null);
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        if (!res.ok) {
          throw new Error('Failed to fetch members');
        }
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const handleMemberClick = (memberId) => {
    setSelectedMember(memberId);
    setMobileMenuOpen(false);
  };

  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/auth/google';
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const handleSort = (criteria) => {
    if (sortBy === criteria) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(criteria);
      setSortOrder('asc');
    }
  };

  const filteredAndSortedMembers = members
    .filter(member => {
      const query = searchQuery.toLowerCase();
      return (
        member.name.toLowerCase().includes(query) ||
        member.email.toLowerCase().includes(query)
      );
    })
    .sort((a, b) => {
      const order = sortOrder === 'asc' ? 1 : -1;
      if (sortBy === 'name') {
        return order * a.name.localeCompare(b.name);
      } else {
        return order * a.email.localeCompare(b.email);
      }
    });

  const renderTeamMembersList = () => {
    if (loading) {
      return (
        <div className="loading-skeleton">
          {[...Array(6)].map((_, index) => (
            <div key={index} className="skeleton-card">
              <div className="skeleton-avatar"></div>
              <div className="skeleton-content">
                <div className="skeleton-line"></div>
                <div className="skeleton-line"></div>
              </div>
            </div>
          ))}
        </div>
      );
    }

    if (error) {
      return (
        <div className="error-message">
          <AlertCircle size={24} />
          <p>{error}</p>
          <button onClick={() => window.location.reload()} className="retry-btn">
            Retry
          </button>
        </div>
      );
    }

    return (
      <div className="dashboard-container">
        <div className="dashboard-header">
          <div className="dashboard-title">
            <h2>Team Dashboard</h2>
            <p className="subtitle">Manage your team's calendar access and availability</p>
          </div>
          <div className="dashboard-actions">
            <button 
              className="primary-action-btn"
              onClick={() => handleMemberClick('all')}
              title="View combined calendar for all team members"
            >
              <span className="icon">ðŸ‘¥</span>
              <span className="button-text">Combined Calendar View</span>
            </button>
          </div>
        </div>

        <div className="team-controls">
          <div className="search-bar">
            <Search size={18} />
            <input
              type="text"
              placeholder="Search members..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
          </div>
          <div className="sort-controls">
            <button
              className={`sort-btn ${sortBy === 'name' ? 'active' : ''}`}
              onClick={() => handleSort('name')}
              title={`Sort by name ${sortOrder === 'asc' ? '(A-Z)' : '(Z-A)'}`}
            >
              Name {sortBy === 'name' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
            </button>
            <button
              className={`sort-btn ${sortBy === 'email' ? 'active' : ''}`}
              onClick={() => handleSort('email')}
              title={`Sort by email ${sortOrder === 'asc' ? '(A-Z)' : '(Z-A)'}`}
            >
              Email {sortBy === 'email' && (sortOrder === 'asc' ? 'â†‘' : 'â†“')}
            </button>
          </div>
        </div>

        <div className="team-grid">
          {filteredAndSortedMembers.map((member) => (
            <div key={member.id} className="team-card" title={`View ${member.name}'s calendar`}>
              <div className="card-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: `hsl(${member.id * 60}, 70%, 60%)`
                }}>
                  {member.name.charAt(0).toUpperCase()}
                </div>
                <div className="member-status">
                  <div className="status-indicator"></div>
                  <span className="status-text">Active</span>
                </div>
              </div>
              <div className="card-body">
                <h3 className="member-name">{member.name}</h3>
                <p className="member-email">{member.email}</p>
              </div>
              <div className="card-footer">
                <button 
                  className="calendar-btn"
                  onClick={() => handleMemberClick(member.id)}
                >
                  <span className="icon">ðŸ“…</span>
                  <span className="button-text">View Schedule</span>
                </button>
              </div>
            </div>
          ))}
        </div>

        {filteredAndSortedMembers.length === 0 && (
          <div className="no-results">
            <p>No members found matching your search criteria</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <>
      <div className={`header ${!user ? 'login-header' : ''} ${mobileMenuOpen ? 'mobile-menu-open' : ''}`}>
        <div className="header-main">
          {user && selectedMember && (
            <button 
              className="back-button" 
              onClick={() => setSelectedMember(null)}
            >
              <ArrowLeft size={20} /> 
              <span className="button-text">Back to Members</span>
            </button>
          )}
          <div className="header-left">
            <Logo />
            <h1 className="header-title">Team Calendar</h1>
          </div>
          {user && (
            <button className="mobile-menu-toggle" onClick={toggleMobileMenu}>
              <Menu size={24} />
            </button>
          )}
        </div>

        <div className={`controls ${mobileMenuOpen ? 'show' : ''}`}>
          {user ? (
            <>
              <select
                value={timezone}
                onChange={(e) => setTimezone(e.target.value)}
                className="timezone-select"
              >
                <option value="Asia/Kolkata">IST</option>
                <option value="UTC">UTC</option>
                <option value="America/New_York">EST</option>
                <option value="Europe/London">GMT</option>
              </select>
              <button className="login-btn logout-btn" onClick={logout}>
                <span>Logout</span>
              </button>
            </>
          ) : (
            <button className="login-btn google-login" onClick={handleLogin}>
              <img src={googleLogo} alt="Google" className="google-icon" />
              <span>Login with Google</span>
            </button>
          )}
        </div>
      </div>

      {user && !selectedMember && renderTeamMembersList()}
    </>
  );
}
