import { useState, useEffect, useCallback, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight, ZoomIn, ZoomOut, Filter } from 'react-feather';
import React from 'react';
import { DayDetailView } from './DayDetailView';

const ZOOM_LEVELS = {
  compact: { height: '40px', fontSize: '0.8rem' },
  normal: { height: '60px', fontSize: '0.9rem' },
  expanded: { height: '80px', fontSize: '1rem' }
};

const EVENT_TYPES = {
  meeting: { color: '#3b82f6', label: 'Meeting' },
  training: { color: '#10b981', label: 'Training' },
  assessment: { color: '#8b5cf6', label: 'Assessment' },
  'half-day': { color: '#ec4899', label: 'Half Day' },
  default: { color: '#6b7280', label: 'Other' }
};

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(true);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });
  const [zoomLevel, setZoomLevel] = useState('normal');
  const [activeFilters, setActiveFilters] = useState([]);
  const [showFilterMenu, setShowFilterMenu] = useState(false);
  const gridRef = useRef(null);

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const groupEventsByDay = useCallback(
    (events) => {
      return getWeekDays().reduce((acc, date) => {
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });

        const dayEvents = events.filter((event) => {
          const eventDate = new Date(event.start.dateTime);
          return (
            eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
            date.toLocaleDateString('en-US', { timeZone: timezone })
          );
        });

        acc[dateString] = dayEvents;
        return acc;
      }, {});
    },
    [getWeekDays, timezone]
  );

  const handleWeekChange = (direction) => {
    setCurrentWeekStart((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
      return newDate;
    });
  };

  // Keyboard navigation
  const handleKeyDown = useCallback((e) => {
    if (e.key === 'ArrowLeft') {
      handleWeekChange('prev');
    } else if (e.key === 'ArrowRight') {
      handleWeekChange('next');
    } else if (e.key === 'Escape') {
      setSelectedDate(null);
    }
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  // Zoom controls
  const handleZoom = (direction) => {
    const levels = Object.keys(ZOOM_LEVELS);
    const currentIndex = levels.indexOf(zoomLevel);
    if (direction === 'in' && currentIndex < levels.length - 1) {
      setZoomLevel(levels[currentIndex + 1]);
    } else if (direction === 'out' && currentIndex > 0) {
      setZoomLevel(levels[currentIndex - 1]);
    }
  };

  // Filter controls
  const toggleFilter = (type) => {
    setActiveFilters(prev => 
      prev.includes(type)
        ? prev.filter(t => t !== type)
        : [...prev, type]
    );
  };

  const filteredEvents = useCallback((dayEvents) => {
    if (!activeFilters.length) return dayEvents;
    return dayEvents.filter(event => 
      activeFilters.includes(event.type || 'default')
    );
  }, [activeFilters]);

  // Event fetching and processing
  useEffect(() => {
    const fetchEvents = async () => {
      if (!user) return;
      setLoading(true);
      try {
        const endDate = new Date(currentWeekStart);
        endDate.setDate(endDate.getDate() + 7);

        const url = selectedMember === 'all'
          ? `http://localhost:5000/api/events/team?start=${currentWeekStart.toISOString()}&end=${endDate.toISOString()}`
          : `http://localhost:5000/api/events/${selectedMember}?start=${currentWeekStart.toISOString()}&end=${endDate.toISOString()}`;

        const res = await fetch(url, { credentials: 'include' });
        if (!res.ok) throw new Error('Failed to fetch events');

        const data = await res.json();
        const groupedEvents = groupEventsByDay(data);
        setEvents(groupedEvents);
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [user, selectedMember, timezone, currentWeekStart]);

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  // Add mobile message for combined view
  if (selectedMember === 'all') {
    return (
      <>
        <div className="combined-calendar-mobile-message">
          <div className="message-icon">ðŸ“±</div>
          <p className="message-text">Combined calendar view is not available on mobile devices.</p>
          <p className="suggestion-text">Please use a tablet or desktop device to view multiple calendars simultaneously.</p>
        </div>
        <div className="calendar-container" data-view="combined">
          <div className="calendar-controls">
            <div className="calendar-header">
              <button 
                className="nav-button" 
                onClick={() => handleWeekChange('prev')}
                aria-label="Previous Week"
              >
                <ChevronLeft size={24} />
                <span className="mobile-hidden">Previous Week</span>
              </button>

              <h2 className="week-range">{getWeekRangeString()}</h2>

              <button 
                className="nav-button" 
                onClick={() => handleWeekChange('next')}
                aria-label="Next Week"
              >
                <span className="mobile-hidden">Next Week</span>
                <ChevronRight size={24} />
              </button>
            </div>

            <div className="view-controls">
              <button
                className="control-btn"
                onClick={() => handleZoom('out')}
                disabled={zoomLevel === 'compact'}
                aria-label="Zoom Out"
              >
                <ZoomOut size={18} />
              </button>
              <button
                className="control-btn"
                onClick={() => handleZoom('in')}
                disabled={zoomLevel === 'expanded'}
                aria-label="Zoom In"
              >
                <ZoomIn size={18} />
              </button>
              <div className="filter-container">
                <button
                  className="control-btn"
                  onClick={() => setShowFilterMenu(!showFilterMenu)}
                  aria-label="Filter Events"
                >
                  <Filter size={18} />
                </button>
                {showFilterMenu && (
                  <div className="filter-menu">
                    {Object.entries(EVENT_TYPES).map(([type, { color, label }]) => (
                      <label key={type} className="filter-option">
                        <input
                          type="checkbox"
                          checked={!activeFilters.length || activeFilters.includes(type)}
                          onChange={() => toggleFilter(type)}
                        />
                        <span className="filter-color" style={{ backgroundColor: color }}></span>
                        <span>{label}</span>
                      </label>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>

          <div 
            className="calendar-grid" 
            ref={gridRef}
            role="grid"
            aria-label="Calendar Grid"
          >
            {getWeekDays().map((date) => {
              const dateString = date.toLocaleDateString('en-US', {
                weekday: 'long',
                month: 'short',
                day: 'numeric',
                timeZone: timezone,
              });

              return (
                <div 
                  key={dateString} 
                  className="day-column"
                  role="gridcell"
                  aria-label={dateString}
                >
                  <div 
                    className="day-header"
                    onClick={() => setSelectedDate(date)}
                    style={{ cursor: 'pointer' }}
                    role="button"
                    tabIndex={0}
                    onKeyPress={(e) => e.key === 'Enter' && setSelectedDate(date)}
                  >
                    <div className="weekday">
                      {date.toLocaleDateString('en-US', {
                        weekday: 'short',
                        timeZone: timezone,
                      })}
                    </div>
                    <div className="date">
                      {date.toLocaleDateString('en-US', {
                        day: 'numeric',
                        timeZone: timezone,
                      })}
                    </div>
                  </div>

                  <div 
                    className="events-list"
                    style={{ 
                      ...ZOOM_LEVELS[zoomLevel],
                      overflowY: 'auto' 
                    }}
                  >
                    {filteredEvents(events[dateString] || []).map((event) => (
                      <div
                        key={event.id}
                        className={`event-item ${event.type || 'default'}`}
                        style={{
                          borderLeftColor: EVENT_TYPES[event.type || 'default'].color
                        }}
                        role="button"
                        tabIndex={0}
                        onClick={() => setSelectedDate(date)}
                        onKeyPress={(e) => e.key === 'Enter' && setSelectedDate(date)}
                        title={`${event.summary}
Time: ${formatTime(event.start.dateTime)} - ${formatTime(event.end.dateTime)}
${selectedMember === 'all' ? `Member: ${event.memberName}` : ''}`}
                      >
                        <div className="event-content">
                          <div className="event-title">{event.summary}</div>
                          <div className="event-time">
                            <span className="time-icon">ðŸ•’</span>
                            {formatTime(event.start.dateTime)} - {formatTime(event.end.dateTime)}
                          </div>
                          {selectedMember === 'all' && (
                            <div className="event-member">
                              <span className="member-icon">ðŸ‘¤</span>
                              {event.memberName}
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                    {events[dateString]?.length === 0 && (
                      <p className="no-events">No events</p>
                    )}
                  </div>
                </div>
              );
            })}
          </div>

          {selectedDate && (
            <DayDetailView
              date={selectedDate}
              events={Object.values(events).flat()}
              onClose={() => setSelectedDate(null)}
              timezone={timezone}
              selectedMember={selectedMember}
            />
          )}
        </div>
      </>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleWeekChange('prev')}>
          <ChevronLeft size={24} />
          <span className="mobile-hidden">Previous Week</span>
        </button>

        <h2 className="week-range">{getWeekRangeString()}</h2>

        <button className="nav-button" onClick={() => handleWeekChange('next')}>
          <span className="mobile-hidden">Next Week</span>
          <ChevronRight size={24} />
        </button>
      </div>

      <div className="calendar-grid">
        {getWeekDays().map((date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          return (
            <div key={dateString} className="day-column">
              <div 
                className="day-header"
                onClick={() => setSelectedDate(date)}
                style={{ cursor: 'pointer' }}
              >
                <div className="weekday">
                  {date.toLocaleDateString('en-US', {
                    weekday: 'short',
                    timeZone: timezone,
                  })}
                </div>
                <div className="date">
                  {date.toLocaleDateString('en-US', {
                    day: 'numeric',
                    timeZone: timezone,
                  })}
                </div>
              </div>

              <div className="events-list">
                {(events[dateString] || []).map((event) => (
                  <EventItem
                    key={event.id}
                    title={event.summary}
                    start={formatTime(event.start.dateTime)}
                    end={formatTime(event.end.dateTime)}
                    memberName={selectedMember === 'all' ? event.memberName : null}
                  />
                ))}
                {events[dateString]?.length === 0 && (
                  <p className="no-events">No events</p>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {selectedDate && (
        <DayDetailView
          date={selectedDate}
          events={Object.values(events).flat()}
          onClose={() => setSelectedDate(null)}
          timezone={timezone}
          selectedMember={selectedMember}
        />
      )}
    </div>
  );
}
