// App.js
import { useState } from 'react';
import Header from './components/Header';
import CalendarGrid from './components/CalendarGrid';
import UserStatus from './components/UserStatus';
import { useAuth } from './context/AuthContext';
import './App.css';

function App() {
  const { user, loading } = useAuth();
  const [selectedMember, setSelectedMember] = useState(null);

  if (loading) return <div className="loading-container">Loading...</div>;

  return (
    <div className="app">
      <Header selectedMember={selectedMember} setSelectedMember={setSelectedMember} />
      <UserStatus />
      <main className="main-content">
        {user ? (
          selectedMember ? (
            <CalendarGrid selectedMember={selectedMember} />
          ) : (
            <div className="auth-warning select-member-prompt">
              Select a team member to view their calendar
            </div>
          )
        ) : (
          <div className="auth-warning login-prompt">
            <h2>Team Calendar</h2>
            <p>Please login to view calendar availability</p>
            <button className="login-btn" onClick={() => window.location.href = 'http://localhost:5000/auth/google'}>
              Login with Google
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
