export default function EventItem({ title, start, end, memberName, duration }) {
  // Calculate the height based on duration (1 hour = 60px)
  const height = Math.max(60, (duration / (60 * 60 * 1000)) * 60);

  return (
    <div 
      className="event-item"
      style={{
        height: `${height}px`,
        position: 'relative',
        margin: '2px 0'
      }}
      data-member={memberName}
    >
      <div className="event-time-bar"></div>
      <div className="event-content">
        <h3 className="event-title">{title || 'Untitled Event'}</h3>
        <div className="event-time">
