import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';

export default function CalendarGrid() {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Memoize the groupEventsByDay function with useCallback
  const groupEventsByDay = useCallback(
    (events) => {
      return events.reduce((acc, event) => {
        const date = new Date(event.start.dateTime);
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });
        if (!acc[dateString]) acc[dateString] = [];
        acc[dateString].push(event);
        return acc;
      }, {});
    },
    [timezone] // Add timezone as a dependency
  );

  // Memoize the formatTime function with useCallback
  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone] // Add timezone as a dependency
  );

  // Fetch events when user or timezone changes
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/availability?timezone=${timezone}`,
          { credentials: 'include' }
        );
        const data = await res.json();
        setEvents(groupEventsByDay(data));
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user) fetchEvents();
  }, [user, timezone, groupEventsByDay]); // Add groupEventsByDay as a dependency

  if (loading) return <div>Loading calendar...</div>;
  if (!user) return <div>Please login to view calendar</div>;

  return (
    <div className="calendar-grid">
      {Object.entries(events).map(([date, dayEvents]) => (
        <div key={date} className="day-column">
          <div className="day-header">{date}</div>
          {dayEvents.map((event) => (
            <EventItem
              key={event.id}
              title={event.summary}
              start={formatTime(event.start.dateTime)}
              end={formatTime(event.end.dateTime)}
            />
          ))}
        </div>
      ))}
    </div>
  );
}
