import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { ArrowLeft } from 'react-feather';
import googleLogo from '../assets/google-logo.svg';
import Logo from './Logo';

export default function Header({ selectedMember, setSelectedMember }) {
  const { user, logout, timezone, setTimezone } = useAuth();
  const [members, setMembers] = useState([]);
  // comment this for the add the memeber
  // const [newMember, setNewMember] = useState({ name: '', email: '', calendarId: '' });
  // const [showAddForm, setShowAddForm] = useState(false);

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const handleMemberClick = (memberId) => {
    setSelectedMember(memberId);
  };

  // const handleAddMember = async () => {
  //   try {
  //     const res = await fetch('http://localhost:5000/api/members', {
  //       method: 'POST',
  //       credentials: 'include',
  //       headers: { 'Content-Type': 'application/json' },
  //       body: JSON.stringify(newMember),
  //     });
  //     const data = await res.json();
  //     setMembers([...members, data]);
  //     setNewMember({ name: '', email: '', calendarId: '' });
  //   } catch (error) {
  //     console.error('Error adding member:', error);
  //   }
  // };

  // comment this for the remove/adding the deletebutton/calendar view button
  // const handleDeleteMember = async (memberId) => {
  //   try {
  //     await fetch(`http://localhost:5000/api/members/${memberId}`, {
  //       method: 'DELETE',
  //       credentials: 'include',
  //     });
  //     setMembers(members.filter(m => m.id !== memberId));
  //     if (selectedMember === memberId) setSelectedMember(null);
  //   } catch (error) {
  //     console.error('Error deleting member:', error);
  //   }
  // };

  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/auth/google';
  };

  const renderTeamMembersList = () => {
    return (
      <div className="dashboard-container">
        <div className="dashboard-header">
          <div className="dashboard-title">
            <h2>Team Dashboard</h2>
            <p className="subtitle">Manage your team's calendar access and availability</p>
          </div>
          <div className="dashboard-actions">
            <button 
              className="primary-action-btn"
              onClick={() => handleMemberClick('all')}
            >
              <span className="icon">üë•</span>
              Combined Calendar View
            </button>
            {/* comment this for the remove/adding the button  */}
            {/* <button 
              className="secondary-action-btn"
              onClick={() => setShowAddForm(!showAddForm)}
            >
              <span className="icon">{showAddForm ? '‚úï' : '+'}</span>
              {showAddForm ? 'Cancel' : 'Add Member'}
            </button> */}
          </div>
        </div>
        {/* comment this for the remove/adding the button  */}
        {/* {showAddForm && (
          <div className="add-member-panel">
            <div className="panel-content">
              <div className="form-header">
                <h3>Add New Team Member</h3>
                <p>Enter the details of the new team member below</p>
              </div>
              <div className="form-grid">
                <div className="form-field">
                  <label>Full Name</label>
                  <input
                    type="text"
                    placeholder="Enter member's name"
                    value={newMember.name}
                    onChange={(e) => setNewMember({ ...newMember, name: e.target.value })}
                  />
                </div>
                <div className="form-field">
                  <label>Email Address</label>
                  <input
                    type="email"
                    placeholder="Enter member's email"
                    value={newMember.email}
                    onChange={(e) => setNewMember({ ...newMember, email: e.target.value })}
                  />
                </div>
                <div className="form-field">
                  <label>Calendar ID</label>
                  <input
                    type="text"
                    placeholder="Enter Google Calendar ID"
                    value={newMember.calendarId}
                    onChange={(e) => setNewMember({ ...newMember, calendarId: e.target.value })}
                  />
                </div>
              </div>
              <button className="submit-btn" onClick={handleAddMember}>
                <span className="icon">‚úì</span>
                Add to Team
              </button>
            </div>
          </div>
        )} */}

        <div className="team-grid">
          {members.map((member) => (
            <div key={member.id} className="team-card">
              <div className="card-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: `hsl(${member.id * 60}, 70%, 60%)`
                }}>
                  {member.name.charAt(0).toUpperCase()}
                </div>
                <div className="member-status">
                  <div className="status-indicator"></div>
                  Active
                </div>
              </div>
              <div className="card-body">
                <h3 className="member-name">{member.name}</h3>
                <p className="member-email">{member.email}</p>
              </div>
              <div className="card-footer">
                <button 
                  className="calendar-btn"
                  onClick={() => handleMemberClick(member.id)}
                >
                  <span className="icon">üìÖ</span>
                  View Schedule
                </button>
                {/* comment this for the remove/adding the deletebutton  */}
                {/* <button
                  className="remove-btn"
                  onClick={() => handleDeleteMember(member.id)}
                >
                  <span className="icon">üóëÔ∏è</span>
                </button> */}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <>
      <div className={`header ${!user ? 'login-header' : ''}`}>
        {user && selectedMember && (
          <button 
            className="back-button" 
            onClick={() => setSelectedMember(null)}
          >
            <ArrowLeft size={24} /> Back to Members
          </button>
        )}
        <div className="header-left">
          <Logo />
          <h1 className="header-title">Team Calendar</h1>
        </div>
        <div className="controls">
          {user ? (
            <>
              <select
                value={timezone}
                onChange={(e) => setTimezone(e.target.value)}
                className="timezone-select"
              >
                <option value="Asia/Kolkata">IST</option>
                <option value="UTC">UTC</option>
                <option value="America/New_York">EST</option>
                <option value="Europe/London">GMT</option>
              </select>
              <button className="login-btn" onClick={logout}>
                <span>Logout</span>
              </button>
            </>
          ) : (
            <button className="login-btn google-login" onClick={handleLogin}>
              <img src={googleLogo} alt="Google" />
              <span>Login with Google</span>
            </button>
          )}
        </div>
      </div>

      {user && !selectedMember && renderTeamMembersList()}
    </>
  );
}
