import React from 'react';
import { useAuth } from '../context/AuthContext';
import { User, Clock } from 'lucide-react';

export default function UserStatus() {
  const { user, timezone, loading } = useAuth();

  if (loading) {
    return (
      <div className="bg-white rounded-lg shadow-sm p-3 mb-6 animate-pulse">
        <div className="h-6 bg-gray-200 rounded w-3/4"></div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-sm p-3 mb-6">
      {user ? (
        <div className="flex items-center justify-between flex-wrap gap-2">
          <div className="flex items-center gap-2">
            <div className="bg-indigo-100 p-1.5 rounded-full">
              <User size={16} className="text-indigo-600" />
            </div>
            <span className="text-gray-600">Logged in as:</span>
            <strong className="text-gray-800">{user.email}</strong>
          </div>
          <div className="flex items-center gap-2 text-sm text-gray-500">
            <Clock size={14} />
            <span>Timezone: {timezone}</span>
          </div>
        </div>
      ) : (
        <div className="flex items-center gap-2 text-gray-600">
          <div className="bg-gray-100 p-1.5 rounded-full">
            <User size={16} className="text-gray-500" />
          </div>
          <span>Please login to view calendar availability</span>
        </div>
      )}
    </div>
  );
}
