import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight } from 'react-feather';

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const groupEventsByDay = useCallback(
    (events) => {
      return getWeekDays().reduce((acc, date) => {
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });

        const dayEvents = events.filter((event) => {
          const eventDate = new Date(event.start.dateTime);
          return (
            eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
            date.toLocaleDateString('en-US', { timeZone: timezone })
          );
        });

        acc[dateString] = dayEvents;
        return acc;
      }, {});
    },
    [getWeekDays, timezone]
  );

  const handleWeekChange = (direction) => {
    setCurrentWeekStart((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
      return newDate;
    });
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const startOfWeek = new Date(currentWeekStart);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(startOfWeek.getDate() + 6);

        let allEvents = [];

        if (selectedMember === 'all') {
          // Fetch events for all members
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName; // Add member name to each event
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          // Fetch events for single member
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
            { credentials: 'include' }
          );
          allEvents = await res.json();
        }

        setEvents(groupEventsByDay(allEvents));
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, currentWeekStart, groupEventsByDay, members]);

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleWeekChange('prev')}>
          <ChevronLeft size={24} />
          <span className="mobile-hidden">Previous Week</span>
        </button>

        <h2 className="week-range">{getWeekRangeString()}</h2>

        <button className="nav-button" onClick={() => handleWeekChange('next')}>
          <span className="mobile-hidden">Next Week</span>
          <ChevronRight size={24} />
        </button>
      </div>

      <div className="calendar-grid">
        {getWeekDays().map((date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          return (
            <div key={dateString} className="day-column">
              <div 
                className="day-header"
                onClick={() => setSelectedDate(date)}
                style={{ cursor: 'pointer' }}
              >
                <div className="weekday">
                  {date.toLocaleDateString('en-US', {
                    weekday: 'short',
                    timeZone: timezone,
                  })}
                </div>
                <div className="date">
                  {date.toLocaleDateString('en-US', {
                    day: 'numeric',
                    timeZone: timezone,
                  })}
                </div>
              </div>

              <div className="events-list">
                {(events[dateString] || []).map((event) => (
                  <EventItem
                    key={event.id}
                    title={event.summary}
                    start={formatTime(event.start.dateTime)}
                    end={formatTime(event.end.dateTime)}
                    memberName={selectedMember === 'all' ? event.memberName : null}
                  />
                ))}
                {events[dateString]?.length === 0 && (
                  <p className="no-events">No events</p>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {selectedDate && (
        <DayDetailView
          date={selectedDate}
          events={Object.values(events).flat()}
          onClose={() => setSelectedDate(null)}
          timezone={timezone}
          selectedMember={selectedMember}
        />
      )}
    </div>
  );
}

// DayDetailView Component
const DayDetailView = ({ date, events, onClose, timezone, selectedMember }) => {
  const [members, setMembers] = useState([]);

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };
    fetchMembers();
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      timeZone: timezone
    });
  };

  const formatTime = (time) => {
    return new Date(time).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    });
  };

  // Filter events for the selected date
  const dateEvents = events.filter(event => {
    const eventDate = new Date(event.start.dateTime);
    return eventDate.toLocaleDateString('en-US', { timeZone: timezone }) === 
           date.toLocaleDateString('en-US', { timeZone: timezone });
  });

  // Get event color based on member
  const getMemberColor = (memberId) => {
    const colors = [
      '#4f46e5', // Indigo
      '#ec4899', // Pink
      '#f97316', // Orange
      '#14b8a6', // Teal
      '#8b5cf6', // Purple
      '#06b6d4', // Cyan
      '#84cc16', // Lime
      '#f43f5e', // Rose
    ];
    return colors[memberId % colors.length];
  };

  // Calculate event position and height
  const calculateEventStyle = (event, hour) => {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);
    const slotStart = new Date(date);
    const slotEnd = new Date(date);

    slotStart.setHours(hour, 0, 0, 0);
    slotEnd.setHours(hour + 1, 0, 0, 0);

    // If event starts before this hour, adjust top position
    const startMinute = eventStart < slotStart ? 0 : eventStart.getMinutes();
    const endMinute = eventEnd > slotEnd ? 60 : eventEnd.getMinutes();

    const top = (startMinute / 60) * 100;
    const height = ((endMinute - startMinute) / 60) * 100;

    return {
      position: 'absolute',
      top: `${top}%`,
      height: `${height}%`,
      width: 'calc(100% - 8px)',
      left: '4px',
    };
  };

  // Check if event should be rendered in this hour slot
  const shouldRenderEvent = (event, hour) => {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);
    const slotStart = new Date(date);
    slotStart.setHours(hour, 0, 0, 0);

    // Only render event in its starting hour
    return eventStart.getHours() === hour || 
           (hour === 0 && eventStart.getHours() === 24); // Handle midnight case
  };

  // Group events by time block
  const getTimeBlocks = () => {
    const blocks = [];

    // Generate a block for each hour (24 hours)
    for (let hour = 0; hour < 24; hour++) {
      const timeLabel = `${hour === 0 ? '12' : hour > 12 ? hour - 12 : hour}:00 ${hour >= 12 ? 'PM' : 'AM'}`;

      const memberSlots = members.map(member => {
        // Get events for this member in this hour
        const memberEvents = dateEvents.filter(event => {
          const isForMember = selectedMember === 'all' 
            ? event.memberName === member.name 
            : event.calendarId === member.calendarId;

          if (!isForMember) return false;

          const eventStart = new Date(event.start.dateTime);
          const eventEnd = new Date(event.end.dateTime);
          const slotStart = new Date(date);
          const slotEnd = new Date(date);

          slotStart.setHours(hour, 0, 0, 0);
          slotEnd.setHours(hour + 1, 0, 0, 0);

          return eventStart < slotEnd && eventEnd > slotStart;
        });

        return (
          <div 
            key={member.id} 
            className="time-slot"
          >
            {memberEvents
              .filter(event => shouldRenderEvent(event, hour))
              .map((event, index) => (
                <div 
                  key={index} 
                  className="event-details"
                  style={{ 
                    ...calculateEventStyle(event, hour),
                    borderLeftColor: getMemberColor(member.id),
                    backgroundColor: `${getMemberColor(member.id)}10`,
                    borderLeft: `4px solid ${getMemberColor(member.id)}`,
                    zIndex: 1
                  }}
                >
                  <div className="event-title">{event.summary}</div>
                  <div className="event-time">
                    {formatTime(event.start.dateTime)} - {formatTime(event.end.dateTime)}
                  </div>
                </div>
            ))}
          </div>
        );
      });

      blocks.push(
        <div key={hour} className="time-block">
          <div className="time-label">{timeLabel}</div>
          <div className="slots-row">
            {memberSlots}
          </div>
        </div>
      );
    }

    return blocks;
  };

  return (
    <div className="day-detail-overlay" onClick={onClose}>
      <div className="day-detail-modal" onClick={e => e.stopPropagation()}>
        <div className="day-detail-header">
          <div>
            <h2>{formatDate(date)}</h2>
            <div className="timezone-display">Timezone: {timezone}</div>
          </div>
          <button className="close-button" onClick={onClose}>âœ•</button>
        </div>

        <div className="availability-grid">
          <div className="member-headers">
            <div className="time-header">Time</div>
            {members.map(member => (
              <div key={member.id} className="member-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: getMemberColor(member.id)
                }}>
                  {member.name.charAt(0)}
                </div>
                <span className="member-name">{member.name}</span>
              </div>
            ))}
          </div>

          <div className="time-blocks">
            {getTimeBlocks()}
          </div>
        </div>
      </div>
    </div>
  );
};
