import React from 'react';
import styled from 'styled-components';

const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
`;

const TimezoneSelect = styled.select`
  padding: 8px 15px;
  border: 2px solid #e0e0e0;
  border-radius: 8px;
`;

const AuthButton = styled.button`
  padding: 8px 20px;
  border-radius: 8px;
  border: none;
  cursor: pointer;
  background: ${(props) => (props.logout ? '#e74c3c' : '#4285f4')};
  color: white;
`;

export default function Header({ user, timezone, onTimezoneChange, onLogout }) {
  return (
    <HeaderContainer>
      <h1>ðŸ“… Calendar Availability</h1>
      <div className="controls">
        {user && (
          <>
            <TimezoneSelect value={timezone} onChange={onTimezoneChange}>
              <option value="Asia/Kolkata">IST</option>
              <option value="UTC">UTC</option>
              <option value="America/New_York">EST</option>
              <option value="Europe/London">GMT</option>
            </TimezoneSelect>
            <AuthButton logout onClick={onLogout}>
              Logout
            </AuthButton>
          </>
        )}
        {!user && (
          <AuthButton onClick={() => (window.location.href = 'http://localhost:5000/auth/google')}>
            Login with Google
          </AuthButton>
        )}
      </div>
    </HeaderContainer>
  );
}
