import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight, Calendar, List, Grid, Square } from 'react-feather';
import React from 'react';

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [viewMode, setViewMode] = useState('week'); // 'day', 'week', 'month', 'year'
  const [currentDate, setCurrentDate] = useState(new Date());
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  // Get days for the week view
  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  // Get days for the month view
  const getMonthDays = useCallback(() => {
    const days = [];
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();

    // First day of the month
    const firstDay = new Date(year, month, 1);
    // Last day of the month
    const lastDay = new Date(year, month + 1, 0);

    // Get the first day to display (might be from previous month)
    const firstDayToDisplay = new Date(firstDay);
    firstDayToDisplay.setDate(firstDayToDisplay.getDate() - firstDayToDisplay.getDay());

    // Generate 42 days (6 weeks) to ensure consistent month view
    for (let i = 0; i < 42; i++) {
      const day = new Date(firstDayToDisplay);
      day.setDate(firstDayToDisplay.getDate() + i);
      days.push(day);
    }

    return days;
  }, [currentDate]);

  // Get months for year view
  const getYearMonths = useCallback(() => {
    const months = [];
    const year = currentDate.getFullYear();

    for (let i = 0; i < 12; i++) {
      const monthDate = new Date(year, i, 1);
      months.push(monthDate);
    }

    return months;
  }, [currentDate]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const getMonthYearString = useCallback(() => {
    return currentDate.toLocaleDateString('en-US', {
      month: 'long',
      year: 'numeric',
      timeZone: timezone,
    });
  }, [currentDate, timezone]);

  const getYearString = useCallback(() => {
    return currentDate.getFullYear().toString();
  }, [currentDate]);

  const getDayString = useCallback(() => {
    return currentDate.toLocaleDateString('en-US', {
      weekday: 'long',
      month: 'long',
      day: 'numeric',
      year: 'numeric',
      timeZone: timezone,
    });
  }, [currentDate, timezone]);

  const getTitleByViewMode = useCallback(() => {
    switch (viewMode) {
      case 'day':
        return getDayString();
      case 'week':
        return getWeekRangeString();
      case 'month':
        return getMonthYearString();
      case 'year':
        return getYearString();
      default:
        return getWeekRangeString();
    }
  }, [viewMode, getDayString, getWeekRangeString, getMonthYearString, getYearString]);

  // Group events by day for the weekly view
  const groupEventsByDay = useCallback(
    (events) => {
      if (viewMode === 'week') {
        return getWeekDays().reduce((acc, date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          const dayEvents = events.filter((event) => {
            const eventDate = new Date(event.start.dateTime);
            return (
              eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
              date.toLocaleDateString('en-US', { timeZone: timezone })
            );
          });

          acc[dateString] = dayEvents;
          return acc;
        }, {});
      } else {
        // For other views, group by date string
        return events.reduce((acc, event) => {
          const eventDate = new Date(event.start.dateTime);
          const dateString = eventDate.toLocaleDateString('en-US', {
            timeZone: timezone
          });

          if (!acc[dateString]) {
            acc[dateString] = [];
          }

          acc[dateString].push(event);
          return acc;
        }, {});
      }
    },
    [getWeekDays, timezone, viewMode]
  );

  const handleDateNavigation = (direction) => {
    switch (viewMode) {
      case 'day':
        setCurrentDate(prevDate => {
          const newDate = new Date(prevDate);
          newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
          return newDate;
        });
        break;
      case 'week':
        setCurrentWeekStart(prev => {
          const newDate = new Date(prev);
          newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
          return newDate;
        });
        // Also update currentDate to match the week
        setCurrentDate(prev => {
          const newDate = new Date(prev);
          newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
          return newDate;
        });
        break;
      case 'month':
        setCurrentDate(prevDate => {
          const newDate = new Date(prevDate);
          newDate.setMonth(newDate.getMonth() + (direction === 'next' ? 1 : -1));
          return newDate;
        });
        break;
      case 'year':
        setCurrentDate(prevDate => {
          const newDate = new Date(prevDate);
          newDate.setFullYear(newDate.getFullYear() + (direction === 'next' ? 1 : -1));
          return newDate;
        });
        break;
      default:
        break;
    }
  };

  // Set current date based on viewMode changes
  useEffect(() => {
    if (viewMode === 'day') {
      // No need to change currentDate for day view
    } else if (viewMode === 'week') {
      // If switching to week view, update the week start based on currentDate
      const newWeekStart = new Date(currentDate);
      newWeekStart.setHours(0, 0, 0, 0);
      newWeekStart.setDate(newWeekStart.getDate() - newWeekStart.getDay());
      setCurrentWeekStart(newWeekStart);
    }
    // For month and year views, currentDate is already correctly set
  }, [viewMode, currentDate]);

  // Handle view mode change
  const handleViewModeChange = (mode) => {
    setViewMode(mode);
  };

  // Check if a date is today
  const isToday = (date) => {
    const today = new Date();
    return date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();
  };

  // Check if a date is in the current month (for month view)
  const isCurrentMonth = (date) => {
    return date.getMonth() === currentDate.getMonth();
  };

  // Get events for a specific date
  const getEventsForDate = (date) => {
    const dateString = date.toLocaleDateString('en-US', { timeZone: timezone });
    return events[dateString] || [];
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        let startDate, endDate;

        // Set the date range based on the view mode
        switch (viewMode) {
          case 'day':
            startDate = new Date(currentDate);
            startDate.setHours(0, 0, 0, 0);
            endDate = new Date(currentDate);
            endDate.setHours(23, 59, 59, 999);
            break;
          case 'week':
            startDate = new Date(currentWeekStart);
            endDate = new Date(startDate);
            endDate.setDate(startDate.getDate() + 6);
            endDate.setHours(23, 59, 59, 999);
            break;
          case 'month':
            startDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
            endDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
            endDate.setHours(23, 59, 59, 999);
            break;
          case 'year':
            startDate = new Date(currentDate.getFullYear(), 0, 1);
            endDate = new Date(currentDate.getFullYear(), 11, 31);
            endDate.setHours(23, 59, 59, 999);
            break;
          default:
            startDate = new Date(currentWeekStart);
            endDate = new Date(startDate);
            endDate.setDate(startDate.getDate() + 6);
            endDate.setHours(23, 59, 59, 999);
        }

        console.log('Date Range:', {
          start: startDate.toISOString(),
          end: endDate.toISOString(),
          timezone
        });

        let allEvents = [];

        if (selectedMember === 'all') {
          // Fetch events for all members
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startDate.toISOString()}&end=${endDate.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          console.log('API Response for all members:', results);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName;
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          // Fetch events for single member
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startDate.toISOString()}&end=${endDate.toISOString()}`,
            { credentials: 'include' }
          );
          const data = await res.json();
          console.log('API Response for single member:', data);
          allEvents = data;
        }

        const groupedEvents = groupEventsByDay(allEvents);
        console.log('Grouped Events:', groupedEvents);
        setEvents(groupedEvents);
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, currentWeekStart, currentDate, viewMode, groupEventsByDay, members]);

  // Render functions for different views
  const renderDayView = () => {
    // Display a single day's events in a timeline format
    const dayEvents = getEventsForDate(currentDate);

    return (
      <div className="day-view">
        <div className="day-timeline">
          {Array.from({ length: 24 }).map((_, hour) => {
            const hourEvents = dayEvents.filter(event => {
              const eventStart = new Date(event.start.dateTime);
              return eventStart.getHours() === hour;
            });

            return (
              <div key={hour} className="hour-slot">
                <div className="hour-label">
                  {hour === 0 ? '12 AM' : hour < 12 ? `${hour} AM` : hour === 12 ? '12 PM' : `${hour - 12} PM`}
                </div>
                <div className="hour-events">
                  {hourEvents.length > 0 ? (
                    hourEvents.map(event => (
                      <EventItem
                        key={event.id}
                        title={event.summary}
                        start={formatTime(event.start.dateTime)}
                        end={formatTime(event.end.dateTime)}
                        memberName={selectedMember === 'all' ? event.memberName : null}
                      />
                    ))
                  ) : (
                    <div className="no-events-hour">No events</div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const renderWeekView = () => {
    return (
      <div className="calendar-grid week-view">
        {getWeekDays().map((date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          return (
            <div key={dateString} className={`day-column ${isToday(date) ? 'current-day' : ''}`}>
              <div 
                className="day-header"
                onClick={() => {
                  setSelectedDate(date);
                  setCurrentDate(date);
                }}
                style={{ cursor: 'pointer' }}
              >
                <div className="weekday">
                  {date.toLocaleDateString('en-US', {
                    weekday: 'short',
                    timeZone: timezone,
                  })}
                </div>
                <div className="date">
                  {date.toLocaleDateString('en-US', {
                    day: 'numeric',
                    timeZone: timezone,
                  })}
                </div>
              </div>

              <div className="events-list">
                {(events[dateString] || []).map((event) => (
                  <EventItem
                    key={event.id}
                    title={event.summary}
                    start={formatTime(event.start.dateTime)}
                    end={formatTime(event.end.dateTime)}
                    memberName={selectedMember === 'all' ? event.memberName : null}
                  />
                ))}
                {events[dateString]?.length === 0 && (
                  <p className="no-events">No events</p>
                )}
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  const renderMonthView = () => {
    const monthDays = getMonthDays();

    return (
      <div className="calendar-grid month-view">
        <div className="weekday-headers">
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
            <div key={day} className="weekday-header">{day}</div>
          ))}
        </div>
        <div className="month-days">
          {monthDays.map((date, index) => {
            const dateKey = date.toISOString();
            const dateString = date.toLocaleDateString('en-US', { timeZone: timezone });
            const dayEvents = events[dateString] || [];

            return (
              <div 
                key={dateKey} 
                className={`month-day ${!isCurrentMonth(date) ? 'other-month' : ''} ${isToday(date) ? 'today' : ''}`}
                onClick={() => {
                  setSelectedDate(date);
                  setCurrentDate(date);
                }}
              >
                <div className="month-day-header">
                  {date.getDate()}
                </div>
                <div className="month-day-events">
                  {dayEvents.length > 0 && (
                    <div className="event-count">
                      {dayEvents.length > 3 
                        ? <>
                            <div className="event-dot"></div>
                            <div className="event-dot"></div>
                            <div className="event-dot"></div>
                            <span className="more-events">+{dayEvents.length - 3}</span>
                          </>
                        : Array(dayEvents.length).fill().map((_, i) => (
                            <div key={i} className="event-dot"></div>
                          ))
                      }
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  const renderYearView = () => {
    const months = getYearMonths();

    return (
      <div className="calendar-grid year-view">
        {months.map(month => {
          const monthName = month.toLocaleDateString('en-US', { month: 'short', timeZone: timezone });
          // Calculate events for this month
          const firstDay = new Date(month.getFullYear(), month.getMonth(), 1);
          const lastDay = new Date(month.getFullYear(), month.getMonth() + 1, 0);

          let eventCount = 0;
          // Count events in this month
          for (const dateStr in events) {
            const eventDate = new Date(dateStr);
            if (eventDate >= firstDay && eventDate <= lastDay) {
              eventCount += events[dateStr].length;
            }
          }

          return (
            <div 
              key={monthName} 
              className={`year-month ${month.getMonth() === new Date().getMonth() && 
                month.getFullYear() === new Date().getFullYear() ? 'current-month' : ''}`}
              onClick={() => {
                setCurrentDate(month);
                setViewMode('month');
              }}
            >
              <div className="month-name">{monthName}</div>
              <div className="mini-month-grid">
                {/* Show a miniature representation of the month */}
                <div className="mini-month-events">
                  {eventCount > 0 && <div className="event-indicator">{eventCount} events</div>}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <div className="calendar-navigation">
          <button className="nav-button" onClick={() => handleDateNavigation('prev')}>
            <ChevronLeft size={24} />
            <span className="mobile-hidden">Previous</span>
          </button>

          <h2 className="calendar-title">{getTitleByViewMode()}</h2>

          <button className="nav-button" onClick={() => handleDateNavigation('next')}>
            <span className="mobile-hidden">Next</span>
            <ChevronRight size={24} />
          </button>
        </div>

        <div className="view-mode-selector">
          <button 
            className={`view-mode-btn ${viewMode === 'day' ? 'active' : ''}`} 
            onClick={() => handleViewModeChange('day')}
            title="Day View"
          >
            <List size={18} />
            <span className="mobile-hidden">Day</span>
          </button>
          <button 
            className={`view-mode-btn ${viewMode === 'week' ? 'active' : ''}`} 
            onClick={() => handleViewModeChange('week')}
            title="Week View"
          >
            <Calendar size={18} />
            <span className="mobile-hidden">Week</span>
          </button>
          <button 
            className={`view-mode-btn ${viewMode === 'month' ? 'active' : ''}`} 
            onClick={() => handleViewModeChange('month')}
            title="Month View"
          >
            <Grid size={18} />
            <span className="mobile-hidden">Month</span>
          </button>
          <button 
            className={`view-mode-btn ${viewMode === 'year' ? 'active' : ''}`}
            onClick={() => handleViewModeChange('year')}
            title="Year View"
          >
            <Square size={18} />
            <span className="mobile-hidden">Year</span>
          </button>
        </div>
      </div>

      <div className="calendar-view-container">
        {viewMode === 'day' && renderDayView()}
        {viewMode === 'week' && renderWeekView()}
        {viewMode === 'month' && renderMonthView()}
        {viewMode === 'year' && renderYearView()}
      </div>

      {selectedDate && (
        <DayDetailView
          date={selectedDate}
          events={Object.values(events).flat()}
          onClose={() => setSelectedDate(null)}
          timezone={timezone}
          selectedMember={selectedMember}
        />
      )}
    </div>
  );
}

// DayDetailView Component
const DayDetailView = ({ date, events, onClose, timezone, selectedMember }) => {
  const [members, setMembers] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };
    fetchMembers();
  }, []);

  // Update current time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      timeZone: timezone
    });
  };

  const formatTime = (time) => {
    const date = new Date(time);
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    }).replace(/\s/g, ''); // Remove extra spaces for consistent display
  };

  // Filter events for the selected date
  const dateEvents = events.filter(event => {
    const eventDate = new Date(event.start.dateTime);
    return eventDate.toLocaleDateString('en-US', { timeZone: timezone }) === 
           date.toLocaleDateString('en-US', { timeZone: timezone });
  });

  // Group events by time block
  const getTimeBlocks = () => {
    const blocks = [];

    // Process the events and track their display status
    const processedEvents = {};
    const relevantMembers = selectedMember === 'all' ? members : members.filter(m => m.id === parseInt(selectedMember));

    // Function to calculate minutes since midnight
    const getMinutesSinceMidnight = (dateStr) => {
      const date = new Date(dateStr);
      return (date.getHours() * 60) + date.getMinutes();
    };

    // Calculate current time indicator position
    const getCurrentTimePosition = () => {
      const now = new Date();
      const selectedDateStr = date.toLocaleDateString('en-US', { timeZone: timezone });
      const currentDateStr = now.toLocaleDateString('en-US', { timeZone: timezone });

      if (selectedDateStr === currentDateStr) {
        const minutes = getMinutesSinceMidnight(now);
        return `${(minutes / 1440) * 100}%`;
      }
      return null;
    };

    relevantMembers.forEach(member => {
      processedEvents[member.id] = new Map();

      const memberEvents = dateEvents.filter(event => {
        if (selectedMember === 'all') {
          return event.memberName === member.name;
        } else {
          return true;
        }
      });

      memberEvents.sort((a, b) => new Date(a.start.dateTime) - new Date(b.start.dateTime));

      memberEvents.forEach(event => {
        const eventKey = `${event.id}-${event.start.dateTime}`;
        const startMinutes = getMinutesSinceMidnight(event.start.dateTime);
        const endMinutes = getMinutesSinceMidnight(event.end.dateTime);

        processedEvents[member.id].set(eventKey, {
          ...event,
          displayed: false,
          startMinutes,
          endMinutes,
          durationMinutes: endMinutes - startMinutes
        });
      });
    });

    const formatHourLabel = (hour) => {
      const timeDate = new Date(date);
      timeDate.setHours(hour, 0, 0, 0);
      return timeDate.toLocaleTimeString('en-US', {
        hour: 'numeric',
        hour12: true,
        timeZone: timezone
      }).replace(/\s/g, ''); // Remove extra spaces for consistent display
    };

    // Generate blocks for each hour
    for (let hour = 0; hour < 24; hour++) {
      const timeLabel = formatHourLabel(hour);
      const hourStartMinutes = hour * 60;
      const hourEndMinutes = (hour + 1) * 60;

      const memberSlots = relevantMembers.map(member => {
        const memberEventsList = Array.from(processedEvents[member.id].values());

        // Get all events that overlap with this hour
        const hourEvents = memberEventsList.filter(event => {
          if (event.displayed) return false;

          // Check if event overlaps with this hour
          const eventOverlapsHour = (
            (event.startMinutes < hourEndMinutes && event.endMinutes > hourStartMinutes) ||
            (event.startMinutes >= hourStartMinutes && event.startMinutes < hourEndMinutes)
          );

          if (eventOverlapsHour) {
            event.displayed = true;
            return true;
          }

          return false;
        });

        const isBusy = hourEvents.length > 0;

        const getEventType = (event) => {
          const titleLower = (event.summary || '').toLowerCase();
          if (titleLower.includes('meeting')) return 'meeting';
          if (titleLower.includes('assessment')) return 'assessment';
          if (titleLower.includes('half day')) return 'half-day';
          if (titleLower.includes('training')) return 'training';
          return 'default';
        };

        const getEventStyles = (event) => {
          // Calculate position based on minutes since start of day
          const startMinutes = event.startMinutes % 60;
          const durationInMinutes = Math.min(
            event.durationMinutes,
            60 - startMinutes // Cap at remaining minutes in the hour
          );

          // Calculate percentage-based positioning
          const topPercentage = (startMinutes / 60) * 100;
          const heightPercentage = (durationInMinutes / 60) * 100;

          return {
            position: 'absolute',
            top: `${topPercentage}%`,
            height: `${heightPercentage}%`,
            left: '0',
            right: '0',
            zIndex: event.startMinutes
          };
        };

        return (
          <div 
            key={member.id} 
            className={`time-slot ${isBusy ? 'busy' : 'available'}`}
          >
            {hourEvents.map((event) => (
              <div 
                key={`${event.id}-${hour}`}
                className={`event-details ${getEventType(event)}`}
                style={getEventStyles(event)}
                data-start-time={event.startMinutes}
              >
                <div className="event-title">
                  {event.summary}
                </div>
                <div className="event-time">
                  <span className="time-icon"></span>
                  {formatTime(event.start.dateTime)} - {formatTime(event.end.dateTime)}
                </div>
              </div>
            ))}
          </div>
        );
      });

      blocks.push(
        <div key={hour} className="time-block">
          <div className="time-label">{timeLabel}</div>
          <div className="slots-row">
            {memberSlots}
            {getCurrentTimePosition() && hour === Math.floor(getMinutesSinceMidnight(currentTime) / 60) && (
              <div 
                className="current-time-indicator" 
                style={{ 
                  top: `${((getMinutesSinceMidnight(currentTime) % 60) / 60) * 100}%` 
                }}
              />
            )}
          </div>
        </div>
      );
    }

    return blocks;
  };

  return (
    <div className="day-detail-overlay" onClick={onClose}>
      <div className="day-detail-modal" onClick={e => e.stopPropagation()}>
        <div className="day-detail-header">
          <div>
            <h2>{formatDate(date)}</h2>
            <div className="timezone-display">Timezone: {timezone}</div>
          </div>
          <button className="close-button" onClick={onClose}></button>
        </div>

        <div className="availability-grid" data-view={selectedMember === 'all' ? 'all' : 'single'}>
          <div className="member-headers">
            <div className="time-header"></div>
            {selectedMember === 'all' ? members.map(member => (
              <div key={member.id} className="member-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: `hsl(${member.id * 60}, 70%, 60%)`
                }}>
                  {member.name.charAt(0)}
                </div>
                <span className="member-name">{member.name}</span>
              </div>
            )) : (
              <div className="member-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: `hsl(${members.find(m => m.id === parseInt(selectedMember))?.id * 60 || 0}, 70%, 60%)`
                }}>
                  {members.find(m => m.id === parseInt(selectedMember))?.name.charAt(0)}
                </div>
                <span className="member-name">
                  {members.find(m => m.id === parseInt(selectedMember))?.name}
                </span>
              </div>
            )}
          </div>

          <div className="time-blocks">
            {getTimeBlocks()}
          </div>
        </div>

        <div className="mobile-view-message">
          <h3>Switch to Desktop View</h3>
          <p>For a better experience viewing multiple team members' schedules, please use a desktop or tablet device.</p>
          <button className="action-button" onClick={onClose}>
            Close
          </button>
        </div>
      </div>
    </div>
  );
};
