import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

export default function Header({ selectedMember, setSelectedMember }) {
  const { user, logout, timezone, setTimezone } = useAuth();
  const [members, setMembers] = useState([]);
  const [newMember, setNewMember] = useState({ name: '', email: '', calendarId: '' });

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const handleMemberClick = (memberId) => {
    setSelectedMember(memberId);
  };

  const handleAddMember = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/members', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newMember),
      });
      const data = await res.json();
      setMembers([...members, data]);
      setNewMember({ name: '', email: '', calendarId: '' });
    } catch (error) {
      console.error('Error adding member:', error);
    }
  };

  const handleDeleteMember = async (memberId) => {
    try {
      await fetch(`http://localhost:5000/api/members/${memberId}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      setMembers(members.filter(m => m.id !== memberId));
      if (selectedMember === memberId) setSelectedMember(null);
    } catch (error) {
      console.error('Error deleting member:', error);
    }
  };

  const handleLogin = () => {
    window.location.href = 'http://localhost:5000/auth/google';
  };

  return (
    <div className="header">
      <h1>📅 Team Calendar</h1>
      <div className="controls">
        {user && (
          <>
            {selectedMember ? (
              <button className="back-button" onClick={() => setSelectedMember(null)}>
                Back to Members
              </button>
            ) : (
              <div className="member-list">
                <h3>Team Members</h3>
                <ul>
                  {members.map((member) => (
                    <li key={member.id}>
                      <span>{member.name}</span>
                      <div>
                        <button onClick={() => handleMemberClick(member.id)}>
                          View
                        </button>
                        <button
                          className="delete-btn"
                          onClick={() => handleDeleteMember(member.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>
                <div className="add-member">
                  <h3>Add New Member</h3>
                  <input
                    type="text"
                    placeholder="Name"
                    value={newMember.name}
                    onChange={(e) => setNewMember({ ...newMember, name: e.target.value })}
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    value={newMember.email}
                    onChange={(e) => setNewMember({ ...newMember, email: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Calendar ID"
                    value={newMember.calendarId}
                    onChange={(e) => setNewMember({ ...newMember, calendarId: e.target.value })}
                  />
                  <button onClick={handleAddMember}>Add Member</button>
                </div>
              </div>
             
            )}
            <select
              value={timezone}
              onChange={(e) => setTimezone(e.target.value)}
              className="timezone-select"
            >
              <option value="Asia/Kolkata">IST</option>
              <option value="UTC">UTC</option>
              <option value="America/New_York">EST</option>
              <option value="Europe/London">GMT</option>
            </select>
          </>
        )}
        {user ? (
          <button className="logout-btn" onClick={logout}>
            Logout
          </button>
        ) : (
          <button className="login-btn" onClick={handleLogin}>
            Login with Google
          </button>
        )}
      </div>
    </div>
  );
}
