import { useState } from 'react';
import Header from './components/Header';
import CalendarGrid from './components/CalendarGrid';
import UserStatus from './components/UserStatus';
import { useAuth } from './context/AuthContext';
import './App.css';

function App() {
  const { user, loading } = useAuth();
  const [selectedMember, setSelectedMember] = useState(null);

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="app">
      <Header selectedMember={selectedMember} setSelectedMember={setSelectedMember} />
      <UserStatus />

      {user && !selectedMember && (
        <div className="member-selection-message">
          Please select a member to view their calendar availability.

          <div className="member-list">
                <h3>Team Members</h3>
                <ul>
                  {members.map((member) => (
                    <li key={member.id}>
                      <span>{member.name}</span>
                      <div>
                        <button onClick={() => handleMemberClick(member.id)}>
                          View
                        </button>
                        <button
                          className="delete-btn"
                          onClick={() => handleDeleteMember(member.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </li>
                  ))}
                </ul>
                <div className="add-member">
                  <h3>Add New Member</h3>
                  <input
                    type="text"
                    placeholder="Name"
                    value={newMember.name}
                    onChange={(e) => setNewMember({ ...newMember, name: e.target.value })}
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    value={newMember.email}
                    onChange={(e) => setNewMember({ ...newMember, email: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder="Calendar ID"
                    value={newMember.calendarId}
                    onChange={(e) => setNewMember({ ...newMember, calendarId: e.target.value })}
                  />
                  <button onClick={handleAddMember}>Add Member</button>
                </div>
              </div>
        </div>
      )}

      <main>
        {user && selectedMember && (
          <CalendarGrid 
            selectedMember={selectedMember} 
            setSelectedMember={setSelectedMember} 
          />
        )}
      </main>
    </div>
  );
}

export default App;
