import React, { useState, useEffect, useCallback } from 'react'; // Add useCallback
import axios from 'axios';
import GlobalStyle from './styles/GlobalStyles';
import Header from './components/Common/Header';
import CalendarView from './components/Calendar/CalendarView';
import Auth from './components/Auth/Auth';

function App() {
  const [user, setUser] = useState(null);
  const [timezone, setTimezone] = useState('Asia/Kolkata');
  const [events, setEvents] = useState([]);

  // Memoize fetchAvailability using useCallback
  const fetchAvailability = useCallback(async () => {
    try {
      const response = await axios.get('/api/availability', {
        params: { timezone },
        withCredentials: true,
      });
      setEvents(response.data);
    } catch (error) {
      console.error('Failed to load calendar data', error);
    }
  }, [timezone]); // Add dependencies here

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const response = await axios.get('/api/me', { withCredentials: true });
        if (response.data?.user?.email) {
          setUser(response.data.user);
          fetchAvailability(); // Call the memoized function
        }
      } catch (error) {
        console.error('Connection error:', error);
      }
    };
    checkAuth();
  }, [fetchAvailability]); // Add fetchAvailability to the dependency array

  const handleLogout = async () => {
    try {
      await axios.post('/logout', {}, { withCredentials: true });
      setUser(null);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <>
      <GlobalStyle />
      <Header
        user={user}
        timezone={timezone}
        onTimezoneChange={(e) => setTimezone(e.target.value)}
        onLogout={handleLogout}
      />
      {user ? (
        <CalendarView events={events} timezone={timezone} />
      ) : (
        <Auth />
      )}
    </>
  );
}

export default App;
