import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight } from 'react-feather';

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });

  const handleBackToWeek = () => {
    setSelectedDate(null);
  };

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const groupEventsByDay = useCallback(
    (events) => {
      return getWeekDays().reduce((acc, date) => {
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });

        const dayEvents = events.filter((event) => {
          const eventDate = new Date(event.start.dateTime);
          return (
            eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
            date.toLocaleDateString('en-US', { timeZone: timezone })
          );
        });

        acc[dateString] = dayEvents;
        return acc;
      }, {});
    },
    [getWeekDays, timezone]
  );

  const handleWeekChange = (direction) => {
    setCurrentWeekStart((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
      return newDate;
    });
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const startOfWeek = new Date(currentWeekStart);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(startOfWeek.getDate() + 6);

        let allEvents = [];

        if (selectedMember === 'all') {
          // Fetch events for all members
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName; // Add member name to each event
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          // Fetch events for single member
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
            { credentials: 'include' }
          );
          allEvents = await res.json();
        }

        setEvents(groupEventsByDay(allEvents));
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, currentWeekStart, groupEventsByDay, members]);

  const renderDayView = () => {
    if (!selectedDate) return null;

    const dateString = selectedDate.toLocaleDateString('en-US', {
      weekday: 'long',
      month: 'long',
      day: 'numeric',
      timeZone: timezone,
    });

    // Get all events for the selected date
    const dayEvents = events[dateString] || [];

    // Get unique member names
    const uniqueMembers = [...new Set(dayEvents.map(event => event.memberName))];

    const calculateEventStyle = (event) => {
      const startTime = new Date(event.start.dateTime);
      const endTime = new Date(event.end.dateTime);
      const startMinutes = startTime.getHours() * 60 + startTime.getMinutes();
      const duration = (endTime - startTime) / (1000 * 60); // duration in minutes

      return {
        top: `${startMinutes}px`,
        height: `${duration}px`,
      };
    };

    return (
      <div className="day-view">
        <div className="day-view-header">
          <button className="back-to-week" onClick={handleBackToWeek}>
            <ChevronLeft size={20} />
            Back to Week View
          </button>
          <h2>{dateString}</h2>
        </div>

        <div className="calendar-legend">
          {uniqueMembers.map(member => (
            <div key={member} className="legend-item" data-member={member}>
              <div className="legend-color" />
              <span>{member}</span>
            </div>
          ))}
        </div>

        <div className="day-view-content">
          <div className="timeline">
            {Array.from({ length: 24 }, (_, i) => (
              <div key={i} className="hour-marker">
                {i === 0 ? '12 AM' : i < 12 ? `${i} AM` : i === 12 ? '12 PM' : `${i - 12} PM`}
              </div>
            ))}
          </div>

          <div className="day-events">
            {/* Current time indicator */}
            {selectedDate.toDateString() === new Date().toDateString() && (
              <div 
                className="current-time-indicator"
                style={{ 
                  top: `${(new Date().getHours() * 60 + new Date().getMinutes())}px`
                }}
              />
            )}

            {Array.from({ length: 24 }, (_, hour) => (
              <div key={hour} className="hour-slot">
                {uniqueMembers.map(member => (
                  <div key={member} className="member-column">
                    {dayEvents
                      .filter(event => 
                        event.memberName === member && 
                        new Date(event.start.dateTime).getHours() === hour
                      )
                      .map(event => (
                        <EventItem
                          key={event.id}
                          title={event.summary}
                          start={formatTime(event.start.dateTime)}
                          end={formatTime(event.end.dateTime)}
                          memberName={event.memberName}
                          style={calculateEventStyle(event)}
                        />
                      ))}
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleWeekChange('prev')}>
          <ChevronLeft size={24} />
          <span className="mobile-hidden">Previous Week</span>
        </button>

        <h2 className="week-range">{getWeekRangeString()}</h2>

        <button className="nav-button" onClick={() => handleWeekChange('next')}>
          <span className="mobile-hidden">Next Week</span>
          <ChevronRight size={24} />
        </button>
      </div>

      {selectedDate ? (
        renderDayView()
      ) : (
        <div className="calendar-grid">
          {getWeekDays().map((date) => {
            const dateString = date.toLocaleDateString('en-US', {
              weekday: 'long',
              month: 'short',
              day: 'numeric',
              timeZone: timezone,
            });

            return (
              <div 
                key={dateString} 
                className="day-column"
                onClick={() => setSelectedDate(new Date(date))}
                style={{ cursor: 'pointer' }}
              >
                <div className="day-header">
                  <div className="weekday">
                    {date.toLocaleDateString('en-US', {
                      weekday: 'short',
                      timeZone: timezone,
                    })}
                  </div>
                  <div className="date">
                    {date.toLocaleDateString('en-US', {
                      day: 'numeric',
                      timeZone: timezone,
                    })}
                  </div>
                </div>

                <div className="events-list">
                  {(events[dateString] || []).map((event) => (
                    <EventItem
                      key={event.id}
                      title={event.summary}
                      start={formatTime(event.start.dateTime)}
                      end={formatTime(event.end.dateTime)}
                      memberName={selectedMember === 'all' ? event.memberName : null}
                    />
                  ))}
                  {events[dateString]?.length === 0 && (
                    <p className="no-events">No events</p>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
