import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight, X } from 'react-feather';

// Member color mapping function
const getMemberColor = (memberName) => {
  const colorMap = {
    'Harsh': { border: '#4f46e5', background: '#eef2ff' },
    'Ayushi': { border: '#ea580c', background: '#fff7ed' },
    'Shubham': { border: '#059669', background: '#ecfdf5' },
    'Chaitanya': { border: '#0891b2', background: '#ecfeff' },
    'Avani': { border: '#db2777', background: '#fdf2f8' }
  };
  return colorMap[memberName?.trim()] || { border: '#6b7280', background: '#f3f4f6' };
};

// DayDetailView Component
const DayDetailView = ({ date, events, onClose, timezone }) => {
  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      timeZone: timezone
    });
  };

  // Calculate event position and height based on time
  const calculateEventStyle = (event) => {
    const startTime = new Date(event.start.dateTime);
    const endTime = new Date(event.end.dateTime);

    // Convert to selected timezone
    const startInTz = new Date(startTime.toLocaleString('en-US', { timeZone: timezone }));
    const endInTz = new Date(endTime.toLocaleString('en-US', { timeZone: timezone }));

    const startMinutes = startInTz.getHours() * 60 + startInTz.getMinutes();
    const endMinutes = endInTz.getHours() * 60 + endInTz.getMinutes();

    const topPosition = (startMinutes / 1440) * 100; // 1440 = minutes in a day
    const height = ((endMinutes - startMinutes) / 1440) * 100;

    const memberColor = event.memberName ? getMemberColor(event.memberName) : { border: '#4f46e5', background: '#eef2ff' };

    return {
      position: 'absolute',
      top: `${topPosition}%`,
      height: `${height}%`,
      left: '60px', // Leave space for time labels
      right: '12px',
      borderLeft: `4px solid ${memberColor.border}`,
      backgroundColor: memberColor.background,
      borderRadius: '6px',
      padding: '8px',
      overflow: 'hidden',
      zIndex: 1
    };
  };

  // Format time in the selected timezone
  const formatTimeInTimezone = (dateTime) => {
    return new Date(dateTime).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    });
  };

  return (
    <div className="day-detail-overlay" onClick={onClose}>
      <div className="day-detail-modal" onClick={e => e.stopPropagation()}>
        <div className="day-detail-header">
          <h2>{formatDate(date)}</h2>
          <button className="close-button" onClick={onClose}>
            <X size={24} />
          </button>
        </div>
        <div className="day-detail-content">
          <div className="timeline-view">
            <div className="timeline-container">
              {/* Time markers */}
              {Array.from({ length: 24 }, (_, hour) => (
                <div key={hour} className="hour-marker">
                  <div className="hour-label">
                    {hour === 0 ? '12 AM' : hour === 12 ? '12 PM' : hour > 12 ? `${hour-12} PM` : `${hour} AM`}
                  </div>
                </div>
              ))}

              {/* Events */}
              <div className="events-container">
                {events.map(event => (
                  <div 
                    key={event.id} 
                    className="detailed-event-item"
                    style={calculateEventStyle(event)}
                  >
                    <div className="event-title">{event.summary}</div>
                    <div className="event-time">
                      {formatTimeInTimezone(event.start.dateTime)} - {formatTimeInTimezone(event.end.dateTime)}
                    </div>
                    {event.memberName && (
                      <div className="event-member">{event.memberName}</div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedDayEvents, setSelectedDayEvents] = useState([]);

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const groupEventsByDay = useCallback(
    (events) => {
      return getWeekDays().reduce((acc, date) => {
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });

        const dayEvents = events.filter((event) => {
          const eventDate = new Date(event.start.dateTime);
          return (
            eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
            date.toLocaleDateString('en-US', { timeZone: timezone })
          );
        });

        acc[dateString] = dayEvents;
        return acc;
      }, {});
    },
    [getWeekDays, timezone]
  );

  const handleWeekChange = (direction) => {
    setCurrentWeekStart((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
      return newDate;
    });
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const startOfWeek = new Date(currentWeekStart);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(startOfWeek.getDate() + 6);

        let allEvents = [];

        if (selectedMember === 'all') {
          // Fetch events for all members
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName; // Add member name to each event
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          // Fetch events for single member
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
            { credentials: 'include' }
          );
          allEvents = await res.json();
        }

        setEvents(groupEventsByDay(allEvents));
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, currentWeekStart, groupEventsByDay, members]);

  const handleDayClick = (date, dayEvents) => {
    setSelectedDate(date);
    setSelectedDayEvents(dayEvents || []);
  };

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleWeekChange('prev')}>
          <ChevronLeft size={24} />
          <span className="mobile-hidden">Previous Week</span>
        </button>

        <h2 className="week-range">{getWeekRangeString()}</h2>

        <button className="nav-button" onClick={() => handleWeekChange('next')}>
          <span className="mobile-hidden">Next Week</span>
          <ChevronRight size={24} />
        </button>
      </div>

      {selectedMember === 'all' && (
        <div className="calendar-legend">
          {members.map((member) => {
            const color = getMemberColor(member.name);
            return (
              <div key={member.id} className="legend-item">
                <div className="color-indicator" style={{ backgroundColor: color.border }}></div>
                <span>{member.name}</span>
              </div>
            );
          })}
        </div>
      )}

      <div className="calendar-grid">
        {getWeekDays().map((date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          const dayEvents = events[dateString] || [];

          return (
            <div 
              key={dateString} 
              className="day-column"
              onClick={() => handleDayClick(date, dayEvents)}
            >
              <div className="day-header">
                <div className="weekday">
                  {date.toLocaleDateString('en-US', {
                    weekday: 'short',
                    timeZone: timezone,
                  })}
                </div>
                <div className="date">
                  {date.toLocaleDateString('en-US', {
                    day: 'numeric',
                    timeZone: timezone,
                  })}
                </div>
              </div>

              <div className="events-list">
                {dayEvents.map((event) => {
                  const memberColor = selectedMember === 'all' && event.memberName
                    ? getMemberColor(event.memberName)
                    : { border: '#4f46e5', background: '#eef2ff' };

                  return (
                    <EventItem
                      key={event.id}
                      title={event.summary}
                      start={formatTime(event.start.dateTime)}
                      end={formatTime(event.end.dateTime)}
                      memberName={selectedMember === 'all' ? event.memberName : null}
                      style={{
                        borderLeftColor: memberColor.border,
                        backgroundColor: memberColor.background
                      }}
                    />
                  );
                })}
                {dayEvents.length === 0 && (
                  <p className="no-events">No events</p>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {selectedDate && (
        <DayDetailView
          date={selectedDate}
          events={selectedDayEvents}
          onClose={() => setSelectedDate(null)}
          timezone={timezone}
        />
      )}
    </div>
  );
}
