import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import EventItem from './EventItem';
import { ChevronLeft, ChevronRight } from 'react-feather';

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState({});
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentWeekStart, setCurrentWeekStart] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - date.getDay());
    return date;
  });

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      const date = new Date(isoString);
      const hours = date.getHours();
      const minutes = date.getMinutes();
      const ampm = hours >= 12 ? 'PM' : 'AM';
      const formattedHours = hours % 12 || 12;
      const formattedMinutes = minutes.toString().padStart(2, '0');

      return `${formattedHours}:${formattedMinutes} ${ampm}`;
    },
    [timezone]
  );

  const getWeekDays = useCallback(() => {
    const days = [];
    const date = new Date(currentWeekStart);
    for (let i = 0; i < 7; i++) {
      const dayDate = new Date(date);
      dayDate.setDate(date.getDate() + i);
      days.push(dayDate);
    }
    return days;
  }, [currentWeekStart]);

  const getWeekRangeString = useCallback(() => {
    const start = new Date(currentWeekStart);
    const end = new Date(start);
    end.setDate(start.getDate() + 6);

    return `${start.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      timeZone: timezone,
    })} - ${end.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      timeZone: timezone,
    })}`;
  }, [currentWeekStart, timezone]);

  const groupEventsByDay = useCallback(
    (events) => {
      return getWeekDays().reduce((acc, date) => {
        const dateString = date.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'short',
          day: 'numeric',
          timeZone: timezone,
        });

        const dayEvents = events.filter((event) => {
          const eventDate = new Date(event.start.dateTime);
          return (
            eventDate.toLocaleDateString('en-US', { timeZone: timezone }) ===
            date.toLocaleDateString('en-US', { timeZone: timezone })
          );
        });

        acc[dateString] = dayEvents;
        return acc;
      }, {});
    },
    [getWeekDays, timezone]
  );

  const handleWeekChange = (direction) => {
    setCurrentWeekStart((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
      return newDate;
    });
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const startOfWeek = new Date(currentWeekStart);
        const endOfWeek = new Date(startOfWeek);
        endOfWeek.setDate(startOfWeek.getDate() + 6);

        let allEvents = [];

        if (selectedMember === 'all') {
          // Fetch events for all members
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName; // Add member name to each event
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          // Fetch events for single member
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startOfWeek.toISOString()}&end=${endOfWeek.toISOString()}`,
            { credentials: 'include' }
          );
          allEvents = await res.json();
        }

        setEvents(groupEventsByDay(allEvents));
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, currentWeekStart, groupEventsByDay, members]);

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleWeekChange('prev')}>
          <ChevronLeft size={24} />
          <span className="mobile-hidden">Previous Week</span>
        </button>

        <h2 className="week-range">{getWeekRangeString()}</h2>

        <button className="nav-button" onClick={() => handleWeekChange('next')}>
          <span className="mobile-hidden">Next Week</span>
          <ChevronRight size={24} />
        </button>
      </div>

      <div className="calendar-grid">
        {getWeekDays().map((date) => {
          const dateString = date.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          });

          return (
            <div 
              key={dateString} 
              className="day-column"
              onClick={() => setSelectedDate(date)}
              style={{ cursor: 'pointer' }}
            >
              <div className="day-header">
                <div className="weekday">
                  {date.toLocaleDateString('en-US', {
                    weekday: 'short',
                    timeZone: timezone,
                  })}
                </div>
                <div className="date">
                  {date.toLocaleDateString('en-US', {
                    day: 'numeric',
                    timeZone: timezone,
                  })}
                </div>
              </div>

              <div className="events-list">
                {(events[dateString] || []).map((event) => (
                  <EventItem
                    key={event.id}
                    title={event.summary}
                    start={formatTime(event.start.dateTime)}
                    end={formatTime(event.end.dateTime)}
                    memberName={selectedMember === 'all' ? event.memberName : null}
                  />
                ))}
                {events[dateString]?.length === 0 && (
                  <p className="no-events">No events</p>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {selectedDate && (
        <DayDetailView
          date={selectedDate}
          events={events[selectedDate.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'short',
            day: 'numeric',
            timeZone: timezone,
          })] || []}
          onClose={() => setSelectedDate(null)}
          timezone={timezone}
        />
      )}
    </div>
  );
}

// DayDetailView Component
const DayDetailView = ({ date, events, onClose, timezone }) => {
  // Format date in the selected timezone
  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      timeZone: timezone
    });
  };

  // Format time in the selected timezone with consistent formatting
  const formatEventTime = (dateTime) => {
    return new Date(dateTime).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    });
  };

  // Generate time slots for 24 hours in the selected timezone
  const timeSlots = Array.from({ length: 24 }, (_, i) => {
    const slotDate = new Date(date);
    slotDate.setHours(i, 0, 0, 0);
    return slotDate.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    });
  });

  // Get events for a specific hour in the selected timezone
  const getEventsForHour = (memberEvents, hour) => {
    const hourStart = new Date(date);
    hourStart.setHours(hour, 0, 0, 0);
    const hourEnd = new Date(date);
    hourEnd.setHours(hour + 1, 0, 0, 0);

    return memberEvents.filter(event => {
      const eventStart = new Date(event.start.dateTime);
      const eventEnd = new Date(event.end.dateTime);

      // Convert times to selected timezone for comparison
      const eventStartInTZ = new Date(eventStart.toLocaleString('en-US', { timeZone: timezone }));
      const eventEndInTZ = new Date(eventEnd.toLocaleString('en-US', { timeZone: timezone }));
      const hourStartInTZ = new Date(hourStart.toLocaleString('en-US', { timeZone: timezone }));
      const hourEndInTZ = new Date(hourEnd.toLocaleString('en-US', { timeZone: timezone }));

      return eventStartInTZ < hourEndInTZ && eventEndInTZ > hourStartInTZ;
    });
  };

  // Calculate event position and height based on its time in the selected timezone
  const calculateEventPosition = (event, hour) => {
    const eventStart = new Date(event.start.dateTime);
    const eventEnd = new Date(event.end.dateTime);

    // Convert to selected timezone
    const eventStartInTZ = new Date(eventStart.toLocaleString('en-US', { timeZone: timezone }));
    const eventEndInTZ = new Date(eventEnd.toLocaleString('en-US', { timeZone: timezone }));

    const hourStart = new Date(date);
    hourStart.setHours(hour, 0, 0, 0);
    const hourStartInTZ = new Date(hourStart.toLocaleString('en-US', { timeZone: timezone }));

    // Calculate position and height
    const startMinutes = (eventStartInTZ.getHours() * 60 + eventStartInTZ.getMinutes()) - (hour * 60);
    const duration = (eventEndInTZ - eventStartInTZ) / (1000 * 60); // duration in minutes

    const top = Math.max(0, (startMinutes / 60) * 100);
    const height = Math.min(100, (duration / 60) * 100);

    return { top: `${top}%`, height: `${height}%` };
  };

  // Get current time indicator position in the selected timezone
  const getCurrentTimePosition = () => {
    const now = new Date();
    const nowInTZ = new Date(now.toLocaleString('en-US', { timeZone: timezone }));
    const dateInTZ = new Date(date.toLocaleString('en-US', { timeZone: timezone }));

    if (dateInTZ.toDateString() !== nowInTZ.toDateString()) return null;

    const minutes = nowInTZ.getHours() * 60 + nowInTZ.getMinutes();
    return `${(minutes / 1440) * 100}%`;
  };

  // Group events by member
  const groupEventsByMember = (events) => {
    return events.reduce((acc, event) => {
      const memberName = event.memberName || 'Unassigned';
      if (!acc[memberName]) {
        acc[memberName] = [];
      }
      acc[memberName].push(event);
      return acc;
    }, {});
  };

  const memberEvents = groupEventsByMember(events);

  return (
    <div className="day-detail-overlay" onClick={onClose}>
      <div className="day-detail-modal" onClick={e => e.stopPropagation()}>
        <div className="day-detail-header">
          <h2>{formatDate(date)}</h2>
          <button className="close-button" onClick={onClose}>âœ•</button>
        </div>
        <div className="day-detail-content">
          <div className="time-grid">
            <div className="time-slots">
              {timeSlots.map((timeSlot, index) => (
                <div key={index} className="time-slot">
                  {timeSlot}
                </div>
              ))}
            </div>
            <div className="member-columns">
              {Object.entries(memberEvents).map(([memberName, memberEvents]) => (
                <div key={memberName} className="member-column">
                  <div className="member-header">
                    <h3 className="member-name">{memberName}</h3>
                  </div>
                  {timeSlots.map((_, hour) => {
                    const hourEvents = getEventsForHour(memberEvents, hour);
                    const status = hourEvents.length > 0 ? 'busy' : 'available';

                    return (
                      <div 
                        key={hour}
                        className={`time-block ${status}`}
                      >
                        {status === 'busy' && hourEvents.map((event, idx) => (
                          <div 
                            key={idx} 
                            className="time-block-event"
                            style={calculateEventPosition(event, hour)}
                          >
                            <div className="event-title">{event.summary || 'Untitled Event'}</div>
                            <div className="event-time">
                              {formatEventTime(event.start.dateTime)} - {formatEventTime(event.end.dateTime)}
                            </div>
                          </div>
                        ))}
                        {hour === new Date().getHours() && (
                          <div 
                            className="current-time-indicator"
                            style={{ top: getCurrentTimePosition() }}
                          />
                        )}
                      </div>
                    );
                  })}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
