import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import { ChevronLeft, ChevronRight } from 'react-feather';

export default function CalendarGrid({ selectedMember, setSelectedMember }) {
  const { user, timezone } = useAuth();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [members, setMembers] = useState([]);
  const [selectedDate, setSelectedDate] = useState(() => {
    const date = new Date();
    date.setHours(0, 0, 0, 0);
    return date;
  });

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };

    if (user) fetchMembers();
  }, [user]);

  const formatTime = useCallback(
    (isoString) => {
      return new Date(isoString).toLocaleTimeString('en-US', {
        hour: 'numeric',
        minute: '2-digit',
        hour12: true,
        timeZone: timezone,
      });
    },
    [timezone]
  );

  const getDateString = useCallback(() => {
    return selectedDate.toLocaleDateString('en-US', {
      weekday: 'long',
      month: 'long',
      day: 'numeric',
      year: 'numeric',
      timeZone: timezone,
    });
  }, [selectedDate, timezone]);

  const handleDateChange = (direction) => {
    setSelectedDate((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
      return newDate;
    });
  };

  // Calculate event position and width
  const calculateEventStyle = (event) => {
    const start = new Date(event.start.dateTime);
    const end = new Date(event.end.dateTime);
    const startHour = start.getHours() + start.getMinutes() / 60;
    const endHour = end.getHours() + end.getMinutes() / 60;
    const duration = endHour - startHour;

    return {
      left: `${(startHour - 8) * 100}px`, // Assuming day starts at 8 AM
      width: `${duration * 100}px`,
      position: 'absolute',
    };
  };

  // Get event type and color
  const getEventTypeAndColor = (event) => {
    const title = (event.summary || '').toLowerCase();
    if (title.includes('design review') || title.includes('ui design')) 
      return { type: 'Design Review', color: '#c084fc', bgColor: '#f3e8ff' };
    if (title.includes('lunch')) 
      return { type: 'Break', color: '#fb923c', bgColor: '#fff7ed' };
    if (title.includes('sprint') || title.includes('implementation')) 
      return { type: 'Sprint', color: '#60a5fa', bgColor: '#eff6ff' };
    if (title.includes('code review')) 
      return { type: 'Review', color: '#c084fc', bgColor: '#f3e8ff' };
    if (title.includes('sync') || title.includes('meeting') || title.includes('one-on-one')) 
      return { type: 'Meeting', color: '#c084fc', bgColor: '#f3e8ff' };
    if (title.includes('stakeholder')) 
      return { type: 'Stakeholder', color: '#c084fc', bgColor: '#f3e8ff' };
    return { type: 'Other', color: '#94a3b8', bgColor: '#f8fafc' };
  };

  // Get member status
  const getMemberStatus = (memberEvents) => {
    const now = new Date();
    const currentEvent = memberEvents.find(event => {
      const start = new Date(event.start.dateTime);
      const end = new Date(event.end.dateTime);
      return now >= start && now <= end;
    });

    if (!currentEvent) return { text: 'Available', color: '#22c55e' };
    if (currentEvent.summary.toLowerCase().includes('lunch')) return { text: 'At lunch', color: '#fb923c' };
    return { text: 'In a meeting', color: '#6366f1' };
  };

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const startOfDay = new Date(selectedDate);
        const endOfDay = new Date(selectedDate);
        endOfDay.setDate(startOfDay.getDate() + 1);

        let allEvents = [];

        if (selectedMember === 'all') {
          const promises = members.map(member =>
            fetch(
              `http://localhost:5000/api/availability/${member.id}?timezone=${encodeURIComponent(
                timezone
              )}&start=${startOfDay.toISOString()}&end=${endOfDay.toISOString()}`,
              { credentials: 'include' }
            ).then(res => res.json())
          );

          const results = await Promise.all(promises);
          results.forEach((memberEvents, index) => {
            const memberName = members[index].name;
            memberEvents.forEach(event => {
              event.memberName = memberName;
            });
            allEvents = [...allEvents, ...memberEvents];
          });
        } else {
          const res = await fetch(
            `http://localhost:5000/api/availability/${selectedMember}?timezone=${encodeURIComponent(
              timezone
            )}&start=${startOfDay.toISOString()}&end=${endOfDay.toISOString()}`,
            { credentials: 'include' }
          );
          allEvents = await res.json();
        }

        setEvents(allEvents);
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user && (selectedMember === 'all' || selectedMember)) {
      fetchEvents();
    }
  }, [user, selectedMember, timezone, selectedDate, members]);

  if (!user) return <div className="auth-warning">Please login to view calendar</div>;

  if (!selectedMember) {
    return (
      <div className="auth-warning">
        Please select a member to view their calendar availability.
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading calendar...</p>
      </div>
    );
  }

  // Render individual member calendar
  const renderIndividualCalendar = () => {
    const member = members.find(m => m.id === selectedMember);
    if (!member) return null;

    return (
      <div className="individual-calendar">
        <div className="member-header">
          <div className="member-info">
            <div className="member-avatar" style={{ backgroundColor: `hsl(${member.id * 60}, 70%, 60%)` }}>
              {member.name.charAt(0)}
            </div>
            <div className="member-details">
              <h3>{member.name}</h3>
              <p>{member.role || 'Team Member'}</p>
            </div>
          </div>
        </div>

        <div className="day-timeline">
          <div className="time-slots">
            {Array.from({ length: 11 }, (_, i) => i + 8).map(hour => (
              <div key={hour} className="time-slot">
                <div className="time-label">
                  {hour === 0 ? '12:00 AM' : hour > 12 ? `${hour-12}:00 PM` : `${hour}:00 AM`}
                </div>
              </div>
            ))}
          </div>

          <div className="events-container">
            {events.map(event => {
              const { type, color, bgColor } = getEventTypeAndColor(event);
              return (
                <div
                  key={event.id}
                  className="calendar-event"
                  style={{
                    ...calculateEventStyle(event),
                    backgroundColor: bgColor,
                    borderLeftColor: color
                  }}
                >
                  <div className="event-content">
                    <div className="event-type" style={{ color }}>
                      {type}
                    </div>
                    <h4>{event.summary}</h4>
                    <div className="event-time">
                      {formatTime(event.start.dateTime)} - {formatTime(event.end.dateTime)}
                    </div>
                    {event.description && (
                      <div className="event-description">
                        {event.description}
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  // Render team calendar
  const renderTeamCalendar = () => {
    return (
      <div className="team-calendar">
        <div className="timeline-view">
          <div className="time-indicators">
            {Array.from({ length: 11 }, (_, i) => i + 8).map(hour => (
              <div key={hour} className="time-marker">
                {hour === 0 ? '12:00 AM' : hour > 12 ? `${hour-12}:00 PM` : `${hour}:00 AM`}
              </div>
            ))}
          </div>

          <div className="member-rows">
            {members.map(member => {
              const memberEvents = events.filter(event => event.memberName === member.name);
              const status = getMemberStatus(memberEvents);

              return (
                <div key={member.id} className="member-row">
                  <div className="member-info">
                    <div className="member-avatar" style={{ backgroundColor: `hsl(${member.id * 60}, 70%, 60%)` }}>
                      {member.name.charAt(0)}
                    </div>
                    <div className="member-details">
                      <h3>{member.name}</h3>
                      <p>{member.role || 'Team Member'}</p>
                      <div className="member-status" style={{ color: status.color }}>
                        <span className="status-dot" style={{ backgroundColor: status.color }}></span>
                        {status.text}
                      </div>
                    </div>
                  </div>

                  <div className="timeline-events">
                    {memberEvents.map(event => {
                      const { type, color, bgColor } = getEventTypeAndColor(event);
                      return (
                        <div
                          key={event.id}
                          className="timeline-event"
                          style={{
                            ...calculateEventStyle(event),
                            backgroundColor: bgColor,
                            borderLeftColor: color
                          }}
                        >
                          <div className="event-content">
                            <div className="event-type" style={{ color }}>
                              {type}
                            </div>
                            <h4>{event.summary}</h4>
                            <div className="event-time">
                              {formatTime(event.start.dateTime)} - {formatTime(event.end.dateTime)}
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="modern-calendar">
      <div className="calendar-header">
        <button className="nav-button" onClick={() => handleDateChange('prev')}>
          <ChevronLeft size={20} />
          <span>Previous</span>
        </button>
        <h2>{getDateString()}</h2>
        <button className="nav-button" onClick={() => handleDateChange('next')}>
          <span>Next</span>
          <ChevronRight size={20} />
        </button>
      </div>

      {selectedMember === 'all' ? renderTeamCalendar() : renderIndividualCalendar()}
    </div>
  );
}

// DayDetailView Component
const DayDetailView = ({ date, events, onClose, timezone, selectedMember }) => {
  const [members, setMembers] = useState([]);

  // Fetch members list
  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/members', {
          credentials: 'include',
        });
        const data = await res.json();
        setMembers(data);
      } catch (error) {
        console.error('Error fetching members:', error);
      }
    };
    fetchMembers();
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      timeZone: timezone
    });
  };

  const formatTime = (time) => {
    return new Date(time).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      timeZone: timezone
    });
  };

  // Filter events for the selected date
  const dateEvents = events.filter(event => {
    const eventDate = new Date(event.start.dateTime);
    return eventDate.toLocaleDateString('en-US', { timeZone: timezone }) === 
           date.toLocaleDateString('en-US', { timeZone: timezone });
  });

  // Group events by time block
  const getTimeBlocks = () => {
    const blocks = [];

    // Generate a block for each hour (24 hours)
    for (let hour = 0; hour < 24; hour++) {
      const timeLabel = `${hour === 0 ? '12' : hour > 12 ? hour - 12 : hour}:00 ${hour >= 12 ? 'PM' : 'AM'}`;

      const memberSlots = members.map(member => {
        // Get events for this member in this hour
        const memberEvents = dateEvents.filter(event => {
          // For team view, match by member name; for individual view match by calendar ID
          const isForMember = selectedMember === 'all' 
            ? event.memberName === member.name 
            : event.calendarId === member.calendarId;

          if (!isForMember) return false;

          // Now check if the event overlaps with this hour
          const eventStartHour = new Date(event.start).getHours();
          const eventEndHour = new Date(event.end).getHours();
          const eventEndMinutes = new Date(event.end).getMinutes();

          // If end time is exactly 00:00, we need to consider it as the end of the previous hour
          const adjustedEndHour = eventEndMinutes === 0 && eventEndHour === 0 ? 24 : eventEndHour;

          // Check if this hour falls within the event's time range
          return hour >= eventStartHour && hour < adjustedEndHour;
        });

        const isBusy = memberEvents.length > 0;

        // Get the color based on the event type
        const getEventColor = (event) => {
          const titleLower = (event.title || '').toLowerCase();
          if (titleLower.includes('meeting')) return '#4f46e5'; // Indigo
          if (titleLower.includes('assessment')) return '#ec4899'; // Pink
          if (titleLower.includes('proxy')) return '#f97316'; // Orange
          if (titleLower.includes('resume') || titleLower.includes('interview')) return '#14b8a6'; // Teal
          return '#4f46e5'; // Default - Indigo
        };

        return (
          <div 
            key={member.id} 
            className={`time-slot ${isBusy ? 'busy' : 'available'}`}
          >
            {memberEvents.map((event, index) => (
              <div 
                key={index} 
                className="event-details"
                style={{ borderLeftColor: getEventColor(event) }}
              >
                <div className="event-title">{event.title}</div>
                <div className="event-time">
                  <span className="time-icon">⏱️</span>
                  {formatTime(new Date(event.start))} - {formatTime(new Date(event.end))}
                </div>
              </div>
            ))}
          </div>
        );
      });

      blocks.push(
        <div key={hour} className="time-block">
          <div className="time-label">{timeLabel}</div>
          <div className="slots-row">
            {memberSlots}
          </div>
        </div>
      );
    }

    return blocks;
  };

  return (
    <div className="day-detail-overlay" onClick={onClose}>
      <div className="day-detail-modal" onClick={e => e.stopPropagation()}>
        <div className="day-detail-header">
          <div>
            <h2>{formatDate(date)}</h2>
            <div className="timezone-display">Timezone: {timezone}</div>
          </div>
          <button className="close-button" onClick={onClose}>✕</button>
        </div>

        <div className="availability-grid">
          {/* Fixed header row with member names */}
          <div className="member-headers">
            <div className="time-header"></div>
            {members.map(member => (
              <div key={member.id} className="member-header">
                <div className="member-avatar" style={{ 
                  backgroundColor: `hsl(${member.id * 60}, 70%, 60%)`
                }}>
                  {member.name.charAt(0)}
                </div>
                <span className="member-name">{member.name}</span>
              </div>
            ))}
          </div>

          {/* Time blocks */}
          <div className="time-blocks">
            {getTimeBlocks()}
          </div>
        </div>
      </div>
    </div>
  );
};
