import React from 'react';
import { format } from 'date-fns';

const EventItem = ({ event, onClick }) => {
  // Format the time to be more readable
  const formatTime = (dateString) => {
    return format(new Date(dateString), 'h:mm a');
  };

  // Determine the event type color
  const getEventTypeColor = (type) => {
    const colors = {
      meeting: '#4a90e2',
      appointment: '#2ecc71',
      reminder: '#f1c40f',
      deadline: '#e74c3c',
      default: '#95a5a6'
    };
    return colors[type?.toLowerCase()] || colors.default;
  };

  return (
    <div
      className="event-item"
      onClick={onClick}
      style={{
        borderLeft: `3px solid ${getEventTypeColor(event.type)}`,
        position: 'relative',
        padding: '8px 12px',
        marginBottom: '8px',
        cursor: 'pointer'
      }}
    >
      <div style={{ fontWeight: '500', marginBottom: '4px' }}>
        {event.title}
      </div>
      <div style={{ 
        fontSize: '0.85rem', 
        color: '#666',
        display: 'flex',
        alignItems: 'center',
        gap: '8px'
      }}>
        <span>üïí {formatTime(event.start)} - {formatTime(event.end)}</span>
        {event.location && (
          <span title={event.location}>üìç {event.location}</span>
        )}
      </div>
      {event.description && (
        <div style={{
          fontSize: '0.8rem',
          color: '#666',
          marginTop: '4px',
          textOverflow: 'ellipsis',
          overflow: 'hidden',
          whiteSpace: 'nowrap'
        }}>
          {event.description}
        </div>
      )}
    </div>
  );
};

export default EventItem;
