// App.js
import { useState } from 'react';
import Header from './components/Header';
import CalendarGrid from './components/CalendarGrid';
import UserStatus from './components/UserStatus';
import { useAuth } from './context/AuthContext';
import './App.css';

function App() {
  const { user, loading } = useAuth();
  const [selectedMember, setSelectedMember] = useState(null);

  if (loading) return <div className="loading-container">Loading...</div>;

  return (
    <div className="app">
      <Header selectedMember={selectedMember} setSelectedMember={setSelectedMember} />

      <main className="main-content">
        <UserStatus />
        {user ? (
          selectedMember ? (
            <CalendarGrid selectedMember={selectedMember} />
          ) : (
            <div className="select-prompt">
              <div className="select-icon">ðŸ‘‰</div>
              Select a team member to view their calendar
            </div>
          )
        ) : (
          <div className="auth-container">
            <h1 className="auth-title">Team Calendar</h1>
            <button className="google-login-btn">
              <img src="google-icon.svg" alt="Google" />
              Login with Google
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
