import styled from 'styled-components';

export const AuthContainer = styled.div`
  text-align: center;
  padding: 2rem;
  max-width: 400px;
  margin: 50px auto;
  background: white;
  border-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);

  h2 {
    color: #2d3436;
    margin-bottom: 1.5rem;
  }

  .disclaimer {
    color: #636e72;
    font-size: 0.9rem;
    margin-top: 1.5rem;
  }
`;

export const AuthButton = styled.button`
  background: #4285f4;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 10px;
  margin: 0 auto;
  transition: transform 0.2s;

  &:hover {
    transform: translateY(-2px);
    background: #357abd;
  }

  &::before {
    content: '';
    background: url('data:image/svg+xml;base64,');
    width: 18px;
    height: 18px;
    display: block;
  }
`;
